def waterShader(shaderCheckBox):
	"""
	
	The waterShader function applies a refractive, slightly blue material to the droplets.
	This is only for NCCA purposes, and should never be used in production.
	
	Args:
		shaderCheckBox:	Checkbox that dictates wether to apply or not to apply the shader to the droplets.
						default: unticked
		
	"""
	
	if shaderCheckBox == True:
		
		
		# variables
		mtlName = "waterDropBlinn"

		# create a shader
		shader = cmds.shadingNode("blinn", name='MTL_%s' %mtlName, asShader=True)

		# set the attribute values
		cmds.setAttr("MTL_%s.color" %mtlName, 0.6, 1, 1, type='double3')
		cmds.setAttr("MTL_%s.transparency" %mtlName, 0.9, 0.9, 0.9, type='double3')
		cmds.setAttr("MTL_%s.diffuse" %mtlName, 1)
		cmds.setAttr("MTL_%s.eccentricity" %mtlName, 0.09)
		cmds.setAttr("MTL_%s.translucence" %mtlName, 0.4)

		# create a shading group
		shading_group = cmds.sets(name="SG_%s" %mtlName, renderable=True, noSurfaceShader=True, empty=True)

		# connect shader to sg surface shader
		cmds.connectAttr('%s.outColor' %shader ,'%s.surfaceShader' %shading_group)

		# select all the objects
		cmds.select("waterDrop*")

		# assign the material to the selection
		cmds.sets( e=True, forceElement='SG_%s' %mtlName)


# script to get the vertex positions of the pre made waterdrops
def vertexWP():
	"""
	
		This function is more of a back end function.
		It allows you to find the vertex positions of pre-modeled waterdrops.
		These coordinates can be used to implement new waterdrop shapes.
			
	"""
	vtxWorldPosition = []
	vtxIndexList = cmds.getAttr(".vrts", multiIndices=True)
	for i in vtxIndexList:
		curPointPosition = cmds.xform(".pnts["+str(i)+"]", query=True, translation=True, worldSpace=True, absolute=True)
		vtxWorldPosition.append(curPointPosition)
